sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // 1. Create an object to store rows from a table
    var grRequest = new GlideRecord('x_58872_needit_needit');

    // 2. Build query
    grRequest.addQuery('active', '=', true);
    // grIncident.QUERY_CONDITION_METHOD('caller_id','=',current.caller_id);
    grRequest.addQuery('u_requested_for', '=', current.u_requested_for);

    // 3. Execute query 
    grRequest.query();

    // Initialize message to list Caller's active Incidents
    var msg = "Caller's other open request: ";

    // 4. Process returned records
    while (grRequest.next()) {
        // add a new line to the message for each of the Caller's active Incidents
        msg = msg + grRequest.getValue('number') + ": " + grRequest.getValue('short_description') + " | ";
    }

    gs.addInfoMessage(msg);

})(current, previous);
